import React, { useState, useEffect } from 'react';
import { Routes, Route, NavLink, Navigate, useLocation } from 'react-router-dom';
import { useAuthState } from '../authentication/authcontext';
import { DASHBOARD_PROFIT_ONESUBMANAGER,DASHBOARD_TODAYTASKS, DASHBOARD_PROFIT, DASHBOARD_RECRUITMENT, DASHBOARD_PROFIT_SUBMANAGERS,DASHBOARD_SUBMANAGERS } from '../consts/api.const';
import { PieChart } from 'react-minimal-pie-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

function TodayTasks(props) {
    const AuthState = props.AuthState;
    const [clients, setClients] = useState([]);
    const [candidates, setCandidates] = useState([]);
    const [subManager, setSubManager] = useState([]);
    useEffect(() => {
        async function fetchData() {
            await fetch(DASHBOARD_TODAYTASKS, {
                method: "POST",
                credentials: "include",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(AuthState)
            })
                .then(
                    response => {
                        if (response.ok) {
                            return response.json();
                        }
                        throw new Error(response);
                    }
                )
                .then(
                    data => {
                        setClients(data.clients);
                        setCandidates(data.candidates);
                    }
                )
                .catch(
                    error => {
                        alert("系統繁忙，請稍後重新載入網頁。")
                    }
                )
        }
        fetchData();
    }, []);

    async function setReadReminder() {
        //pop out a hint to remind user to read the message
        const reminder = document.getElementById("reminder");
        reminder.style.display = "block";
        setTimeout(function () {
            reminder.style.display = "none";
        }
            , 2000);    

            




    }
    return (
        <div className='bounceInAnimation d-flex flex-column align-items-center justify-content-center '>
            <Modal >
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Woohoo, you're reading this text in a modal!
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" >
                        Close
                    </Button>
                    <Button variant="primary" >
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
            <div className='w-90 align-items-center'>
                <h2 className='text-center my-2'>今天事項</h2>
                <div className="w-100">
                    <h3>客人</h3>
                    <div className="overflow-auto">
                        <table className="table table_style" style={{ minWidth: "700px" }}>
                            <thead>
                                <th scope="col">姓名</th>
                                <th scope="col">項目</th>
                                <th scope="col">生意額</th>
                                <th scope="col">生意類別</th>
                                <th scope="col">備註</th>
                                <th scope="col">查閱</th>
                            </thead>
                            <tbody>
                                {!!(clients !== undefined) &&
                                    clients.map((client, index) => {
                                        if(client.meeting.read == "no"){
                                        return (
                                            <tr key={index}>
                                                <th scope="row">{client.clientName}</th>
                                                <td>{client.meeting.item.join("，")}</td>
                                                <td>{(client.meeting.profit !== undefined) && client.meeting.profit}</td>
                                                <td>{client.meeting.businessCategories.join("，")}</td>
                                                <td>{client.meeting.note !== undefined ?
                                                    client.meeting.note.split("\n").map(
                                                        line => <div>{line}</div>)
                                                    : ""}
                                                </td>
                                                <td><input type="checkbox" onClick={setReadReminder}/>{client.meeting.read}</td>
                                            </tr>
                                        );
                                        }
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                    <h3>招募者</h3>
                    <div className="overflow-auto">
                        <table className="table table_style" style={{ minWidth: "700px" }}>
                            <thead>
                                <th scope="col">姓名</th>
                                <th scope="col">項目</th>
                                <th scope="col">類別</th>
                                <th scope="col">備註</th>
                                <th scope="col">查閱</th>
                            </thead>
                            <tbody>
                                {!!(candidates !== undefined) &&
                                    candidates.map((candidate, index) => {
                                        
                                            return (
                                                <tr>
                                                    <th scope="row">{candidate.candidateName}</th>
                                                    <td>{candidate.interview.item.join("，")}</td>
                                                    <td>{candidate.interview.categories.join("，")}</td>
                                                    <td>{candidate.interview.note !== undefined ?
                                                        candidate.interview.note.split("\n").map(
                                                            line => <div>{line}</div>)
                                                        : ""}
                                                    </td>
                                                    <td><input type="checkbox"/></td>
                                                </tr>
                                            );
                                        
                                        
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                    <h3>下線</h3>
                    <div className="overflow-auto">
                        <table className="table table_style" style={{ minWidth: "700px" }}>
                            <thead>
                                <th scope="col">姓名</th>
                                <th scope="col">項目</th>
                                <th scope="col">類別</th>
                                <th scope="col">備註</th>
                                <th scope="col">查閱</th>
                            </thead>
                            <tbody>
                                <tr>
                                    <th scope="row">Test</th>
                                    <td>123</td>
                                    <td>123</td>
                                    <td>123</td>
                                    <td><input type="checkbox"/></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

function Profit(props) {
    const AuthState = props.AuthState;
    const [profitData, setProfitData] = useState([]);
    const [subManagersData, setSubManagersData] = useState([]);
    const [fetchingData, setFetchingData] = useState(true);
    const [showSubManagers, setShowSubManagers] = useState(false);
    const [fetchBtnStatus, setFetchBtnStatus] = useState(true);
    const [subManagersList, setSubManagersList] = useState();


    async function fetchSubManagers(list){
         await fetch(DASHBOARD_PROFIT_SUBMANAGERS, {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(AuthState)
        })
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error(response);
            })
            .then(data => {
                setSubManagersData(data.body);
                console.log(data.body);
                setShowSubManagers(true);
            })
            .catch(err => {
                alert("系統錯誤，請重新載入網頁。")
            });
        
    }
    async function fetchSubManagerslist(e) {
        let subManagers = [];
        await fetch(DASHBOARD_SUBMANAGERS, {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json",
                },
                body: JSON.stringify(AuthState)
        })
            .then( response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error(response);
            } )
            .then( data => {
                let i = 0;
                let subManagersList = data.body.subManagers;
                let times = data.body.subManagers.length
                // /10+data.body.subManagers.length%10;

                
                console.log(subManagersList,times);
                setFetchBtnStatus(false);
                const asyncFetch = async () => {
                    for(i = 0; i < times; i++){
                        await fetch(DASHBOARD_PROFIT_ONESUBMANAGER, {
                            method: "POST",
                            credentials: "include",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({
                                AuthState: AuthState,
                                subManager:subManagersList[i],
                            })
                        })
                            .then(response => {
                                if (response.ok) {
                                    return response.json();
                                }
                                throw new Error(response);
                            }).then(data => {
                                subManagers.push(data.body["subManager"]);
                            }).catch(err => {
                                alert("系統錯誤，請重新載入網頁。")
                            });
                        
                }
            }
                
                asyncFetch().then(() => {
                    let temp = {subManagers};
                    setSubManagersData(temp);
                    setShowSubManagers(true);
                    setFetchBtnStatus(true);
                }).catch(err => {
                    alert("系統錯誤，請重新載入網頁。")
                });

         
           })

         
    }

   
    

    useEffect(() => {
        setProfitData([]);
        fetch(DASHBOARD_PROFIT, {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(AuthState)
        })
            .then((response) => {
