                                { title: "睇 Proposal", value: profitData.self.items[2] * 100, color: "#7C7A9B" },
                                { title: "Try Closing", value: profitData.self.items[3] * 100, color: "#B7888A" },
                                { title: "Networking", value: profitData.self.items[4] * 100, color: "#EEAE6A" },
