import React, { useState, useEffect } from 'react';
import { Routes, Route, NavLink, Navigate, useLocation } from 'react-router-dom';
import { useAuthState } from '../authentication/authcontext';
import { DASHBOARD_PROFIT_2_SUBSUBMANAGERS, DASHBOARD_TODAYTASKS, DASHBOARD_PROFIT, DASHBOARD_RECRUITMENT, DASHBOARD_PROFIT_SUBMANAGERS} from '../consts/api.const';
import { PieChart } from 'react-minimal-pie-chart';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';



function TodayTasks(props) {
    const AuthState = props.AuthState;  
    const [clients, setClients] = useState([]);
    const [candidates, setCandidates] = useState([]);
    const [subManager, setSubManager] = useState([]);
    const [isRead, setIsRead] = useState(false);
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
