import React, { useEffect } from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FORGET_COMPARE_SECURITY, FORGET_GET_SECURITY,LOGIN_PATH, RESUME_SESSION_PATH } from '../consts/api.const';
import { useAuthState } from '../authentication/authcontext.jsx';



export default function Forgetpassword(){


    const [fetching, setFetching] = useState(false);
    const [fetching2, setFetching2] = useState(false);
    const [userExist, setUserExist] = useState(false);
    const [hasSecurity, setHasSecurity] = useState(false);
    const [security, setSecurity] = useState({});
    const [correct, setCorrect] = useState(false);
    
    const navigate = useNavigate();
    
    async function handleSubmit(event) {
        event.preventDefault();
        setFetching(true);

        let user = document.getElementById("name").value;
        await fetch(FORGET_GET_SECURITY, {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                AuthState: {
                    username: user
                }
                })
            })
         .then( (res) => {
            if (res.ok) {
                return (res.json());
            }
        })
         .then( (data) => {
            if(data.status === "success"){
                const foundUser = data.body.foundUser;
                console.log(foundUser);
                if(foundUser.securityQuestion1 != null && foundUser.securityQuestion2 != null ){
                    console.log("has security");
                    setHasSecurity(true);
                    setUserExist(true);
                    setSecurity(foundUser);
                    
                }
                else{
                    alert("還沒有設定安全問題");
                    setFetching(false); 
                }
            }else{
                alert("查無此人");
                setFetching(false);
            }
            
        })

    }

    async function handleSubmit2(event) {
        event.preventDefault();
        setFetching2(true);
